import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [ventas, setVentas] = useState([]);
  const [nuevaVenta, setNuevaVenta] = useState({
    fecha_venta: '',
    metodo_pago: '',
    total_venta: 0,
    total_pedido: 0,
    nombre: '',
    telefono: '',
    documento: '',
    correo: ''
  });

  // Obtener todas las ventas al cargar la p치gina
  useEffect(() => {
    axios.get('http://localhost:9000/api/ventas')
      .then(response => {
        setVentas(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  // Funci칩n para crear una nueva venta
  const crearVenta = () => {
    axios.post('http://localhost:9000/api/ventas', nuevaVenta)
      .then(response => {
        setVentas([...ventas, response.data]);
        setNuevaVenta({
          fecha_venta: '',
          metodo_pago: '',
          total_venta: 0,
          total_pedido: 0,
          nombre: '',
          telefono: '',
          documento: '',
          correo: ''
        });
      })
      .catch(error => {
        console.error('Error creating venta:', error);
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Ventas</h1>
        {/* Mostrar las ventas existentes */}
        <ul>
          {ventas.map(venta => (
            <li key={venta._id}>
              Fecha de Venta: {venta.fecha_venta}, Total: {venta.total_venta}
            </li>
          ))}
        </ul>
        {/* Formulario para crear una nueva venta */}
        <form onSubmit={crearVenta}>
          <input
            type="text"
            placeholder="Fecha de venta"
            value={nuevaVenta.fecha_venta}
            onChange={e => setNuevaVenta({ ...nuevaVenta, fecha_venta: e.target.value })}
          />
          {/* Otros campos del formulario para la nueva venta */}
          <button type="submit">Crear Venta</button>
        </form>
      </header>
    </div>
  );
}

export default App;
