import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [ventas, setVentas] = useState([]);
  const [nuevaVenta, setNuevaVenta] = useState({});

  // Obtener todas las ventas al cargar la página
  useEffect(() => {
    axios.get('http://localhost:9000/api/ventas')
      .then(response => {
        setVentas(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  // Función para crear una nueva venta
  const crearVenta = () => {
    axios.post('http://localhost:9000/api/ventas', nuevaVenta)
      .then(response => {
        setVentas([...ventas, response.data]);
        setNuevaVenta({});
      })
      .catch(error => {
        console.error('Error creating venta:', error);
      });
  };

  // Otras funciones CRUD como actualizarVenta, eliminarVenta, etc.

  return (
    <div className="App">
      <header className="App-header">
        <h1>Ventas</h1>
        {/* Aquí puedes mostrar las ventas existentes */}
        <ul>
          {ventas.map(venta => (
            <li key={venta._id}>
              {venta.fecha_venta} - {venta.total_venta}
            </li>
          ))}
        </ul>
        {/* Formulario para crear una nueva venta */}
        <form onSubmit={crearVenta}>
          <input
            type="text"
            placeholder="Fecha de venta"
            value={nuevaVenta.fecha_venta || ''}
            onChange={e => setNuevaVenta({ ...nuevaVenta, fecha_venta: e.target.value })}
          />
          {/* Otros campos del formulario para la nueva venta */}
          <button type="submit">Crear Venta</button>
        </form>
      </header>
    </div>
  );
}

export default App;
