import { useState } from 'react';

const EditarProductosModal = ({
  isOpen,
  onClose,
  ventaId,
  venta,
  editarVentaProductos,
}) => {
  const [editedProducts, setEditedProducts] = useState(venta.productos);

  const handleEdit = () => {
    editarVentaProductos(ventaId, editedProducts);
    // Aquí, 'editarVentaProductos' es la función que enviará los cambios al backend
  };

  return (
    <div className={`modal ${isOpen ? 'open' : ''}`}>
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h2>Editar Productos de Venta</h2>
        <ul>
          {editedProducts.map((producto, index) => (
            <li key={index}>
              <input
                type="text"
                value={producto.nombre}
                onChange={(e) => {
                  const newProducts = [...editedProducts];
                  newProducts[index].nombre = e.target.value;
                  setEditedProducts(newProducts);
                }}
              />
              <input
                type="number"
                value={producto.precio}
                onChange={(e) => {
                  const newProducts = [...editedProducts];
                  newProducts[index].precio = parseFloat(e.target.value);
                  setEditedProducts(newProducts);
                }}
              />
            </li>
          ))}
        </ul>
        <button onClick={() => setEditedProducts([...editedProducts, { nombre: '', precio: 0 }])}>
          Agregar Producto
        </button>
        <button onClick={handleEdit}>Guardar Cambios</button>
      </div>
    </div>
  );
};

export default EditarProductosModal;
