import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import './App.css';

Modal.setAppElement('#root');

function App() {
  const [ventas, setVentas] = useState([]);
  const [nuevaVenta, setNuevaVenta] = useState({
    fecha_venta: '',
    metodo_pago: '',
    total_venta: 0,
    total_pedido: 0,
    nombre: '',
    telefono: '',
    documento: '',
    correo: ''
  });
  const [modalIsOpen, setModalIsOpen] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:9000/api/ventas')
      .then(response => {
        setVentas(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const crearVenta = () => {
    axios.post('http://localhost:9000/api/ventas', nuevaVenta)
      .then(response => {
        setVentas([...ventas, response.data]);
        setNuevaVenta({
          fecha_venta: '',
          metodo_pago: '',
          total_venta: 0,
          total_pedido: 0,
          nombre: '',
          telefono: '',
          documento: '',
          correo: ''
        });
        setModalIsOpen(false);
      })
      .catch(error => {
        console.error('Error creating venta:', error);
      });
  };

  return (
    <div>
      <h1>Ventas</h1>
      <button onClick={() => setModalIsOpen(true)}>Agregar Nueva Venta</button>
      <table>
        <thead>
          <tr>
            <th>Fecha de Venta</th>
            <th>Método de Pago</th>
            <th>Total de Venta</th>
            {/* Agrega más encabezados si es necesario */}
          </tr>
        </thead>
        <tbody>
          {ventas.map(venta => (
            <tr key={venta._id}>
              <td>{venta.fecha_venta}</td>
              <td>{venta.metodo_pago}</td>
              <td>{venta.total_venta}</td>
              {/* Agrega más datos si es necesario */}
            </tr>
          ))}
        </tbody>
      </table>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
      >
        <h2>Agregar Nueva Venta</h2>
        <form onSubmit={crearVenta}>
          {/* Campos del formulario para la nueva venta */}
        </form>
      </Modal>
    </div>
  );
}

export default App;
