import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [ventas, setVentas] = useState([]);
  const [nuevaVenta, setNuevaVenta] = useState({
    fecha_venta: '',
    metodo_pago: '',
    total_venta: 0,
    total_pedido: 0,
    nombre: '',
    telefono: '',
    documento: '',
    correo: ''
  });

  useEffect(() => {
    axios.get('http://localhost:9000/api/ventas')
      .then(response => {
        setVentas(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const crearVenta = () => {
    axios.post('http://localhost:9000/api/ventas', nuevaVenta)
      .then(response => {
        setVentas([...ventas, response.data]);
        setNuevaVenta({
          fecha_venta: '',
          metodo_pago: '',
          total_venta: 0,
          total_pedido: 0,
          nombre: '',
          telefono: '',
          documento: '',
          correo: ''
        });
      })
      .catch(error => {
        console.error('Error creating venta:', error);
      });
  };

  return (
    <div>
      <h1>Ventas</h1>
      <ul>
        {ventas.map(venta => (
          <li key={venta._id}>
            <p>Fecha de Venta: {venta.fecha_venta}</p>
            <p>Método de Pago: {venta.metodo_pago}</p>
            <p>Total de Venta: {venta.total_venta}</p>
            <p>total pedido: {venta.total_pedido}</p>
            <p>Nombre: {venta.nombre}</p>
            <p>telefono: {venta.telefono}</p>
            <p>Documento: {venta.documento}</p>
            <p>Correo: {venta.correo}</p>


          </li>
        ))}
      </ul>

      <h2>Agregar Nueva Venta</h2>
      <form onSubmit={crearVenta}>
        <input
          type="text"
          placeholder="Fecha de venta"
          value={nuevaVenta.fecha_venta}
          onChange={e => setNuevaVenta({ ...nuevaVenta, fecha_venta: e.target.value })}
        />
        <input
          type="text"
          placeholder="Método de pago"
          value={nuevaVenta.metodo_pago}
          onChange={e => setNuevaVenta({ ...nuevaVenta, metodo_pago: e.target.value })}
        />
        <input
          type="number"
          placeholder="Total de venta"
          value={nuevaVenta.total_venta}
          onChange={e => setNuevaVenta({ ...nuevaVenta, total_venta: e.target.value })}
        />
        {/* Agrega más campos de venta al formulario si es necesario */}
        <button type="submit">Crear Venta</button>
      </form>
    </div>
  );
}

export default App;
