import React, { useState, useEffect } from 'react';
import axios from 'axios';
import jsPDF from 'jspdf';
import Modal from 'react-modal';
import './App.css';

Modal.setAppElement('#root');

function App() {
  const [ventas, setVentas] = useState([]);
  const [filteredVentas, setFilteredVentas] = useState([]);
  const [nuevaVenta, setNuevaVenta] = useState({
    fecha_venta: '',
    metodo_pago: '',
    total_venta: 0,
    total_pedido: 0,
    nombre: '',
    telefono: '',
    documento: '',
    correo: ''
  });
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(4);
  const [searchTerm, setSearchTerm] = useState('');
  const [productos, setProductos] = useState([]); // Nuevo estado para almacenar la lista de productos
  const [selectedProduct, setSelectedProduct] = useState(''); // Nuevo estado para almacenar el producto seleccionado
  const [productoNombre, setProductoNombre] = useState(''); // Nuevo estado para almacenar el nombre del producto a agregar
  const [productoPrecio, setProductoPrecio] = useState(0); // Nuevo estado para almacenar el precio del producto a agregar

  useEffect(() => {
    // Obtener la lista de productos disponibles al cargar la página
    axios.get('http://localhost:9000/api/productos')
      .then(response => {
        setProductos(response.data);
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });
  }, []);

  useEffect(() => {
    axios.get('http://localhost:9000/api/ventas')
      .then(response => {
        setVentas(response.data);
        setFilteredVentas(response.data); // Inicialmente, mostrar todas las ventas
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleKeyPress = e => {
    if (e.key === 'Enter') {
      const filtered = ventas.filter(venta =>
        venta.documento.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredVentas(filtered);
    }
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredVentas.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = pageNumber => setCurrentPage(pageNumber);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setNuevaVenta({ ...nuevaVenta, [name]: value });
  };

  const crearVenta = (e) => {
    e.preventDefault(); // Evita que el formulario se envíe automáticamente

    const selectedDate = new Date(nuevaVenta.fecha_venta); // Convertir la fecha seleccionada a objeto Date
  const currentDate = new Date(); // Obtener la fecha actual

  if (selectedDate.toISOString().slice(0, 10) !== currentDate.toISOString().slice(0, 10)) {
    alert('Selecciona la fecha actual'); // Mostrar un mensaje de alerta si la fecha no es la actual
    return;
  }

    // Validar el campo de correo electrónico
  const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailPattern.test(nuevaVenta.correo)) {
    alert('Ingrese un correo electrónico válido');
    return;
  }

  setNuevaVenta({ ...nuevaVenta, fecha_venta: currentDate });
    // Validar otros campos
    const numberFields = ['total_venta', 'total_pedido', 'telefono'];
    const invalidFields = [];

    numberFields.forEach(field => {
      if (isNaN(parseFloat(nuevaVenta[field]))) {
        invalidFields.push(field);
      }
    });

    if (invalidFields.length > 0) {
      alert('Los campos ' + invalidFields.join(', ') + ' deben ser números');
      return;
    }

    axios.post('http://localhost:9000/api/ventas', nuevaVenta)
      .then(response => {
        setVentas([...ventas, response.data]);
        setNuevaVenta({
          fecha_venta: '',
          metodo_pago: '',
          total_venta: 0,
          total_pedido: 0,
          nombre: '',
          telefono: '',
          documento: '',
          correo: ''
        });
        setModalIsOpen(false);
      })
      .catch(error => {
        console.error('Error creating venta:', error);
      });
  };

  const editarVenta = (id) => {
    // Aquí puedes abrir un modal o redirigir a una página para editar la venta
    console.log(`Editar venta con ID ${id}`);
  };

  const eliminarVenta = (id) => {
    axios.delete(`http://localhost:9000/api/ventas/${id}`)
      .then(() => {
        const updatedVentas = ventas.filter(venta => venta._id !== id);
        setVentas(updatedVentas);
        const updatedFilteredVentas = filteredVentas.filter(venta => venta._id !== id);
        setFilteredVentas(updatedFilteredVentas);
      })
      .catch(error => {
        console.error('Error deleting venta:', error);
      });
  };

  const generarReportePDF = () => {
    if (Array.isArray(currentItems)) {
      const doc = new jsPDF();
      let y = 15;

      doc.text('Reporte de Ventas', 15, 10);

      currentItems.forEach(venta => {
        doc.text(`Fecha de Venta: ${venta.fecha_venta}`, 15, y);
        doc.text(`Método de Pago: ${venta.metodo_pago}`, 15, y + 10);
        doc.text(`Total de Venta: ${venta.total_venta}`, 15, y + 20);
        doc.text(`Total de Pedido: ${venta.total_pedido}`, 15, y + 30);
        doc.text(`Nombre: ${venta.nombre}`, 15, y + 40);
        doc.text(`Teléfono: ${venta.telefono}`, 15, y + 50);
        doc.text(`Documento: ${venta.documento}`, 15, y + 60);
        doc.text(`Correo: ${venta.correo}`, 15, y + 70);
        y += 90;
        if (y >= 280) {
          doc.addPage();
          y = 15;
        }
      });

      doc.save('reporte_ventas.pdf');
    } else {
      console.error('currentItems no es un array:', currentItems);
    }
  };

  return (
    <div>
      <h1>Ventas</h1>
      <input
        type="text"
        placeholder="Buscar por documento"
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        onKeyPress={handleKeyPress}
      />

      <button onClick={() => setModalIsOpen(true)}>Agregar Nueva Venta</button>

      <table>
        <thead>
          <tr>
            <th>Fecha de Venta</th>
            <th>Método de Pago</th>
            <th>Total de Venta</th>
            <th>Total de Pedido</th>
            <th>Nombre</th>
            <th>Teléfono</th>
            <th>Documento</th>
            <th>Correo</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {currentItems.map(venta => (
            <tr key={venta._id}>
            <td>{venta.fecha_venta}</td>
            <td>{venta.metodo_pago}</td>
            <td>{venta.total_venta}</td>
            <td>{venta.total_pedido}</td>
            <td>{venta.nombre}</td>
            <td>{venta.telefono}</td>
            <td>{venta.documento}</td>
            <td>{venta.correo}</td>
            <td>
              <button onClick={() => editarVenta(venta._id)}>Editar</button>
              <button onClick={() => eliminarVenta(venta._id)}>Eliminar</button>
            </td>
          </tr>
          ))}
        </tbody>
      </table>

      <div className="pagination">
        {Array.from({ length: Math.ceil(filteredVentas.length / itemsPerPage) }, (_, index) => (
          <button key={index + 1} onClick={() => paginate(index + 1)}>
            {index + 1}
          </button>
        ))}
      </div>

      <button onClick={generarReportePDF}>Generar Reporte PDF</button>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
      >
        <h2>Agregar Nueva Venta</h2>
        <form onSubmit={crearVenta}>
    <input
      type="date" // Cambiar el tipo a "date"
      placeholder="Fecha de venta"
      name="fecha_venta"
      value={nuevaVenta.fecha_venta}
      onChange={handleInputChange}
    />

          <input
            type="text"
            placeholder="Método de pago"
            value={nuevaVenta.metodo_pago}
            onChange={e => setNuevaVenta({ ...nuevaVenta, metodo_pago: e.target.value })}
          />
          <input
            type="number"
            placeholder="Total de venta"
            value={nuevaVenta.total_venta}
            onChange={e => setNuevaVenta({ ...nuevaVenta, total_venta: e.target.value })}
          />
          <input
            type="number"
            placeholder="Total de pedido"
            value={nuevaVenta.total_pedido}
            onChange={e => setNuevaVenta({ ...nuevaVenta, total_pedido: e.target.value })}
          />
          <input
            type="text"
            placeholder="Nombre"
            value={nuevaVenta.nombre}
            onChange={e => setNuevaVenta({ ...nuevaVenta, nombre: e.target.value })}
          />
          <input
            type="text"
            placeholder="Teléfono"
            value={nuevaVenta.telefono}
            onChange={e => setNuevaVenta({ ...nuevaVenta, telefono: e.target.value })}
          />
          <input
            type="text"
            placeholder="Documento"
            value={nuevaVenta.documento}
            onChange={e => setNuevaVenta({ ...nuevaVenta, documento: e.target.value })}
          />
          <input
            type="text"
            placeholder="Correo"
            value={nuevaVenta.correo}
            onChange={e => setNuevaVenta({ ...nuevaVenta, correo: e.target.value })}
          />
          <button type="submit">Crear Venta</button>
        </form>
      </Modal>
    </div>
  );
}

export default App;
